import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./components/loginPage";
import EmployeeDashboard from "./components/EmployeeDashboard";
import ManagerDashboard from "./components/ManagerDashboard";
import ProtectedRoute from "./components/ProtectedRoute";
import "./styles.css";

function Sidebar(){
  return (
    <aside className="sidebar">
      <div className="brand">
        <div className="avatar">A</div>
        <div>
          <h3>Admin</h3>
          <div style={{fontSize:12, color:"#dfeeff"}}>Enterprise Assistant</div>
        </div>
      </div>
      <nav className="nav">
        <a className="active">Chat</a>
        <a>Documents</a>
        <a>FAQs</a>
        <a>Workflows</a>
        <a>Analytics</a>
      </nav>
      <div style={{marginTop:"auto", fontSize:13, color:"#cfe6ff"}}>Logout</div>
    </aside>
  )
}

function App(){
  return (
    <BrowserRouter>
      <div className="app-shell">
        <Sidebar />
        <main className="main">
          <Routes>
            <Route path="/login" element={<LoginPage/>} />
            <Route path="/" element={<Navigate to="/login" replace />} />
            <Route path="/employee" element={<ProtectedRoute role="employee"><EmployeeDashboard/></ProtectedRoute>} />
            <Route path="/manager" element={<ProtectedRoute role="manager"><ManagerDashboard/></ProtectedRoute>} />
          </Routes>
        </main>
      </div>
    </BrowserRouter>
  );
}

export default App;
